mapper.select:
select  from stock_history t
where t.code=#{code}
order by t.curr_date desc

mapper.delete:
delete from stock_history where code=#{code}
and date_format(curr_date,'%Y%m%d') >=date_format( #{startDate,jdbcType=TIMESTAMP},'%Y%m%d')
and date_format(curr_date,'%Y%m%d') <=date_format( #{endDate,jdbcType=TIMESTAMP},'%Y%m%d')

mapper.select:
select  from stock_history t
where t.code=#{code}
and curr_date  < #{endDate}
order by curr_date desc
limit 1

select  from stock_history t
where t.code=#{code}

    and curr_date > #{startDate}


    and curr_date < #{endDate}

order by t.curr_date asc

select

from stock_history t
where t.code=#{stockHistoryQueryDto.code}

    and curr_date > #{stockHistoryQueryDto.startDate}


    and curr_date < #{stockHistoryQueryDto.endDate}


    and date_format(curr_date,'%m') = #{stockHistoryQueryDto.month}


    and date_format(curr_date,'%d') >= #{stockHistoryQueryDto.startDayNum}


    and date_format(curr_date,'%d') <= #{stockHistoryQueryDto.endDayNum}

order by date_format(t.curr_date,'%Y') desc,date_format(t.curr_date,'%m') asc,
date_format(t.curr_date,'%d') asc

mapper.delete:
delete from stock_history where code=#{code}
and date_format(curr_date,'%Y%m%d') >=date_format( #{startDate,jdbcType=TIMESTAMP},'%Y%m%d')
and date_format(curr_date,'%Y%m%d') <=date_format( #{endDate,jdbcType=TIMESTAMP},'%Y%m%d')
